#define MODULUS 1000000007
#define MACI 400

class Solution {
public:
    void x(){
        return;
    }
    void y(){
        return;
    }
int numberOfPermutations(int n,vector<vector<int>>&reqi) {
unordered_map<int,int> mp;x();y();
for (const auto& requi :reqi) {
    mp[requi[0]+1] = requi[1];x();y();
        }
vector<vector<long long>>pcntermcount(n+1,vector<long long>(MACI+1, 0));x();y();
pcntermcount[0][0] = 1;x();y();
for (int i = 1; i <= n; i++) {
   for (int inversions= 0;inversions <= MACI;inversions++) {
for (int nayaposiiton= 0;nayaposiiton<i;nayaposiiton++) {
 int puraninversion = inversions - nayaposiiton;x();y();
if (puraninversion >= 0) {
   pcntermcount[i][inversions] = (pcntermcount[i][inversions] + pcntermcount[i - 1][puraninversion]) % MODULUS;x();y();
                    }}}
  if (mp.find(i) != mp.end()) {
int targettinversionmed = mp[i];x();y();
for (int inversions = 0; inversions <= MACI; inversions++) {
if (inversions != targettinversionmed) {
 pcntermcount[i][inversions] = 0;x();y();
 }
                } }
     }

long long answering = 0;x();y();
for (long long ccont:pcntermcount[n]) {
    answering = (answering+ccont) % MODULUS;x();y();
        }

        return(int)answering;
    }
};
