class Solution {
public:
    const int MOD = 1e9+7;
    int add(int a, int b){
        return (a+b)%MOD;
    }
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        ios_base::sync_with_stdio(false);
        cin.tie(0);
        cout.tie(0);

        map<int, int>req;
        for(auto r:requirements) req[r[0]+1]=r[1];
        vector<vector<int>>dp(n+1, vector<int>(409, 0));
        dp[0][0]=1;
        for(int i=1;i<=n;i++){
            for(int inv=0;inv<409;inv++)
                for(int j = 0;j<i;j++)
                    if(inv>=j)
                        dp[i][inv] = add(dp[i][inv], dp[i-1][inv-j]);
            if(req.find(i)!=req.end())
                for(int inv =0;inv<409;inv++)
                    if(inv != req[i])
                        dp[i][inv]=0;
        }
        int res=0;
        for(int x:dp[n])
            res=add(res, x);
        return res; 
    }
};
