#define ll long long
ll M=1e9+7;
class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& req) {
        vector<vector<ll>> dp(303,vector<ll>(402,0));

        map<int,int> mp;
        for(auto e:req){
            mp[e[0]+1]=e[1];
        }
        dp[1][0]=1;
        if(mp[1]!=0) dp[1][0]=0;
        for(int i=2; i<=n; i++){
            int sum=0;
            for(int j=0; j<min(401,i*(i+1)/2); j++){
                sum=(sum+dp[i-1][j])%M;
                if(j-i>=0) sum=((sum-dp[i-1][j-i])%M+M)%M;
                dp[i][j]=sum;
            }
            if(mp.find(i)!=mp.end()){
                for(int j=0; j<min(401,i*(i+1)/2); j++){
                    if(j!=mp[i]) dp[i][j]=0;
                }
            }
        }
        return dp[n][mp[n]];
    }
};
