class Solution {
public:

    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int mod=1e9+7;
       map<int, int> m;
        for (auto i: requirements) {
            m[i[0]+1] = i[1];
        }
      vector<vector<long>> dp(n+1, vector<long>(401, 0));
        dp[0][0] = 1;
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= 400; j++) {
                for (int k= 0; k < i; k++) {
                    if (j-k >= 0) {
                        dp[i][j] +=dp[i - 1][j-k];
                        dp[i][j]%=mod;
                    }
                }
            }
            if (m.find(i)!=m.end()) {
                int req =m[i];
                for (int j= 0; j <=400; j++) {
                    if (j !=req) 
                        dp[i][j] = 0;

                }
            }
        }
        long long ans = 0;
        for (auto j :dp[n]) {
            {
                ans+=j;
                ans%=mod;
            }
        }

        return ans;

    }
};
