class Solution {
public:
    int MOD = 1e9 + 7;
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int maxi = 400;
        vector<vector<int>> dp(n + 1, vector<int>(maxi + 1, 0));
        dp[0][0] = 1;
        vector<int> reqInv(n, -1);
        for (auto& req : requirements) {
            reqInv[req[0]] = req[1];
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= maxi; j++) {
                for (int k = 0; k < i; ++k) {
                    if (j >= k) {
                        dp[i][j] = (dp[i][j] + dp[i - 1][j - k]) % MOD;
                    }
                }
            }
            if (reqInv[i - 1] != -1) {
                int targetInv = reqInv[i - 1];
                for (int j = 0; j <= maxi; j++) {
                    if (j != targetInv) {
                        dp[i][j] = 0;
                    }
                }
            }
        }
        return dp[n][reqInv[n - 1]];
    }
};
