#include <bits/stdc++.h>
#define sz size()
#define bk back()
#define fi first
#define se second

using namespace std;
typedef long long ll;
typedef pair<int, int> pii;

const int MOD = 1e9 + 7;

class Solution {

  public:
    int numberOfPermutations(int n, vector<vector<int>> &v) {
        unordered_map<int, int> mp;
        for (int i = 0; i < v.sz; i++)
            mp[v[i][0]] = v[i][1];

        if (mp.count(0) && mp[0] != 0)
            return 0;

        vector<vector<ll>> dp(n, vector<ll>(401, -1));
        dp[0][0] = 1;

        for (int i = 0; i < n - 1; i++) {
            for (int j = 0; j <= 400; j++) {
                if (dp[i][j] == -1)
                    continue;

                if (mp.count(i) && mp[i] != j)
                    continue;

                for (int k = j; k <= min(400, j + i + 1); k++) {
                    if (dp[i + 1][k] == -1)
                        dp[i + 1][k] = 0;

                    dp[i + 1][k] = (dp[i + 1][k] + dp[i][j]) % MOD;
                }
            }
        }

        return dp[n - 1][mp[n - 1]];
    }
};
