class Solution {
public:
    int n ;
    vector<int> rq;
    static const int mod = 1000000007;
    vector<vector<long long>>  dp;
    int solve(int i, int j){
        if ( i == n ){
            return 1;
        }
        long long &ans = dp[i][j];
        if (ans  != -1)
            return ans;
        ans = 0;
        if (rq[i] != -1){
            if (j<=rq[i] and rq[i] - j <= i)
                ans = solve(i + 1 , rq[i]);
            return ans;

        }
        int rm = 400;

        for (int tv = 0; tv <= min( i ,(rm - j)) ; tv++ )
            ans = (ans + 1ll*solve( i + 1 , j + tv))%mod;
        return ans;

    }
    int numberOfPermutations(int N, vector<vector<int>>& rqs) {
        n = N;
        rq = vector<int> ( n , -1 );
        dp = vector<vector<long long>>  ( n ,vector<long long>  ( 401, -1));
        for (auto v: rqs)
            rq[v[0]] = v[1];
        return solve(0 , 0);

    }
};
