const int MOD =1e9+7;
const int MI = 401;

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& re) {
        map<int, int> rm;

        for (const auto& r : re) {
            rm[r[0] + 1] = r[1];
        }
        vector<vector<long>> pc(n + 1, vector<long>(MI + 1, 0));
        pc[0][0] = 1;

        for (int l = 1; l< n+1; ++l) {
            for (int i = 0; i < MI+1; ++i) {
                for (int np = 0; np <=l-1; ++np) {
                    int pi = i - np;
                    if (pi >= 0) {
                        pc[l][i] = (pc[l][i] + pc[l - 1][pi]) % MOD;
                    }
                }
            }

            if (rm.count(l)) {
                int ti = rm[l];
                for (int i = 0; i <= MI; ++i) {
                    if (i != ti) {
                        pc[l][i] = 0;
                    }
                }
            }
        }

         long r = 0;
        for (long c : pc[n]) {
            r = (r + c) % MOD;
        }

        return static_cast<int>(r);
    }
};
