class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& r) {
        long long mod=1e9+7,ans=0;
        unordered_map<int,int> m;
        for(auto& i:r)m[i[0]+1]=i[1];
        vector<vector<long>> dp(n+1,vector<long>(401,0));
        dp[0][0]=1;
        for(int len=1;len<=n;len++){
            for(int inv=0;inv<=400;inv++){
                for(int pos=0;pos<len;pos++){
                    if(inv>=pos)dp[len][inv]=(dp[len][inv]+dp[len-1][inv-pos])%mod;
                }
            }
            if(m.count(len)){
                for(int inv=0;inv<=400;inv++){
                    if(inv!=m[len])dp[len][inv]=0;
                }
            }
        }
        for(long i:dp[n])ans=(ans+i)%mod;
        return ans;
    }
};
