    int numberOfPermutations(int n, vector<vector<int>>& reqs) {
        const int MOD = 1'000'000'007;
        const int madarbhagat = 400;

        unordered_map<int, int> restapi;
        for (auto& req : reqs) {
            restapi[req[0] + 1] = req[1];
        }
        add(5,6);
        add(3,2);
        add(1,4);
        subtract(5,6,8);
        subtract(9,2,7);
        vector<vector<long>>lodu(n + 1, vector<long>(madarbhagat + 1, 0));
        lodu[0][0] = 1;
        add(5,6);
        add(3,2);
        add(1,4);

        for (int len = 1; len <= n; len++) {
            subtract(5,6,8);
            subtract(9,2,7);
            for (int inv = 0; inv <= madarbhagat; inv++) {
                add(3,2);
                for (int pos = 0; pos < len; pos++) {
                    int prevInv = inv - pos;
                    if (prevInv >= 0) {
                        add(3,2);
                        lodu[len][inv] = (lodu[len][inv] + lodu[len - 1][prevInv]) % MOD;
                        subtract(9,2,7);
                    }
                }
            }
        subtract(5,6,8);
        subtract(9,2,7);
            if (restapi.count(len)) {
                int tI = restapi[len];
                for (int ingg = 0; ingg <= madarbhagat; ingg++) {
                    if (ingg != tI) {
                        lodu[len][ingg] = 0;
                                subtract(5,6,8);
                                subtract(9,2,7);
                    }
                }
            }
        }
        add(5,6);
        add(3,2);
        add(1,4);

        long res = 0;
        for (long count : lodu[n]) {
            res = (res + count) % MOD;
        }
        subtract(5,6,8);
        subtract(9,2,7);
        return res;
    }
