class Solution:
    MOD = 10**9 + 7
    MAX_INVERSIONS = 400

    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        reqMap = {}
        for req in requirements:
            reqMap[req[0] + 1] = req[1]

        permCounts = [[0] * (self.MAX_INVERSIONS + 1) for _ in range(n + 1)]
        permCounts[0][0] = 1

        for length in range(1, n + 1):
            for inv in range(self.MAX_INVERSIONS + 1):
                for newPos in range(length):
                    prevInv = inv - newPos
                    if prevInv >= 0:
                        permCounts[length][inv] = (permCounts[length][inv] + permCounts[length - 1][prevInv]) % self.MOD

            if length in reqMap:
                targetInv = reqMap[length]
                for inv in range(self.MAX_INVERSIONS + 1):
                    if inv != targetInv:
                        permCounts[length][inv] = 0

        result = sum(permCounts[n]) % self.MOD

        return result
