class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        const int MOD = 1e9 + 7;
        int K = min(405, n * (n + 1));
        vector <vector <int> > dp(n, vector <int> (K, 0));
        dp[0][0] = 1;
        vector <int> was(n, -1);
        for (auto it : requirements) {
            was[it[0]] = it[1];
        }
        if (was[0] > 0) return 0;
        for (int i = 1; i < n; i ++) {
            for (int j = 0; j < K; j ++) {
                if (was[i - 1] != -1 && j != was[i - 1]) continue;
                for (int z = 0; z <= i; z ++) {
                    //cout << i << ' ' << j << ' ' << ' ' << z << ' ' << dp[i - 1][j] << '\n';
                    if (j + z < K) {
                        dp[i][j + z] += dp[i - 1][j];
                        dp[i][j + z] %= MOD;
                    }
                }
            }
        }
        int ans = 0;
        for (int i = 0; i < K; i ++) {
            if (was[n - 1] != -1 && was[n - 1] != i) continue;
            //cout << "| " << i << '\n';
            ans += dp[n - 1][i];
            ans %= MOD;
        }
        return ans;
    }
};
