class Solution
{
private:
    const int MOD = 1000000007;

public:
    int numberOfPermutations(int n, vector<vector<int>> &requirements)
    {
        long long ans = 0;
        unordered_map<int, int> freq;
        for (auto x : requirements)
            freq[x[0] + 1] = x[1];

        vector<vector<long>> cache(n + 1, vector<long>(403, 0));
        cache[0][0] = 1;
        for (int i = 1; i <= n; ++i)
        {
            for (int j = 0; j <= 402; ++j)
            {
                for (int k = 0; k < i; ++k)
                {
                    int prevj = j - k;
                    if (prevj >= 0)
                        cache[i][j] = (cache[i][j] + cache[i - 1][prevj]) % MOD;
                                }
            }

            if (freq.find(i) != freq.end())
            {
                int targetj = freq[i];
                for (int j = 0; j <= 402; ++j)
                {
                    if (j != targetj)
                    {
                        cache[i][j] = 0;
                    }
                }
            }
        }
        for (auto x : cache[n])
            ans = (ans + x) % MOD;
        return ans;
    }
};
