class Solution
{
#define ll long long
    const int MOD = 1e9 + 7;
    const int maxi = 400;
    vector<vector<ll>> dp;

public:
    int numberOfPermutations(int n, vector<vector<int>> &req)
    {
        ll ans = 0;
        dp = vector<vector<ll>>(n + 1, vector<ll>(maxi + 1, 0));
        dp[0][0] = 1;

        unordered_map<int, int> r;

        for (auto &it : req)
            r[it[0] + 1] = it[1];

        for (int i = 1; i <= n; i++)
        {
            for (int inv = 0; inv <= maxi; inv++)
            {
                for (int di = 0; di < i && di <= inv; di++)
                    dp[i][inv] = (dp[i - 1][inv - di] + dp[i][inv]) % MOD;
            }

            if (r.find(i) == r.end())
                continue;

            int t = r[i];
            for (int inv = 0; inv <= maxi; ++inv)
            {
                if (inv != t)
                    dp[i][inv] = 0;
            }
        }

        for (auto &it : dp[n])
            ans = (ans + it) % MOD;

        return ans % MOD;
    }
};
