class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& req) {
        int mod=1000000007,maxi=400;
        unordered_map<int,int>mp;
        for(auto i:req)
        {
            mp[i[0]+1]=i[1];
        }
        vector<vector<long>>v(n+1,vector<long>(maxi+1,0));
        v[0][0]=1;
        for(int i=1;i<=n;i++)
        {
            for(int j=0;j<=maxi;j++)
            {
                for(int k=0;k<i;k++)
                {
                    int a=j-k;
                    if(a>=0)
                    {
                        v[i][j]=(v[i][j]+v[i-1][a])%mod;
                    }
                }
            }
            if(mp.count(i))
            {
                int b=mp[i];
                for(int j=0;j<=maxi;j++)
                {
                    if(j!=b)v[i][j]=0;
                }
            }
        }
        long ans=0;
        for(auto i:v[n])
        {
            ans=(ans+i)%mod;
        }
        return ans;
    }
};
