class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& v) {
        map<int,int>mp;
        int k = 0;
        for(auto it : v){
            mp[it[0] + 1] = it[1];
            k = max(k,it[1]);
        }
        int mod = 1e9 + 7;
        vector<vector<long long>>dp(n+1,vector<long long>(k+1,0));
        dp[0][0] = 1;
        for(int i = 1 ; i <= n ; i++){
            for(int j = 0 ; j <= k ; j++){
                dp[i][j] = dp[i-1][j];
                for(int l = 1  ; l < i ; l++){
                    if(j - l >= 0){
                        dp[i][j] = (dp[i][j]%mod + dp[i-1][j-l]%mod)%mod;
                        dp[i][j] %= mod;
                    }
                }
            }
            if(mp.find(i)!=mp.end()){
                for(int j = 0 ; j <= k ; j++){
                    if(j!=mp[i]){
                        dp[i][j] = 0;
                    }
                }
            }
        }
        long long ans = 0;
        for(int i = 0 ; i <= k ; i++){
            ans = ans%mod + dp[n][i]%mod;
            ans %= mod;
        }
        return ans;
    }
};
