#include <vector>
using namespace std;

class Solution {
public:
    const int MOD = 1e9 + 7;

    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        unordered_map<int,int>mp;
        int mx=500;
        for(auto x:requirements){
            mp[x[0]+1]=x[1];
        }
        vector<vector<long long>>dp(n+1,vector<long long>(mx+1));
        dp[0][0]=1;
        for(int i=1;i<=n;i++){
            for(int j=0;j<=mx;j++){
                for(int k=0;k<i;k++){
                    int p=j-k;
                    if(p>=0){
                        dp[i][j]=(dp[i][j]+dp[i-1][p])%MOD;
                    }
                }
            }
            if(mp.count(i))
            {
                int no=mp[i];
                for(int j=0;j<=mx;j++){
                    if(j!=no){
                        dp[i][j]=0;
                    }
                }
            }
        }
        long long ans=0;
        for(auto x:dp[n]){
            ans+=x;
            ans%=MOD;
        }
        return ans;
    }
};
