class Solution {
public:
    int numberOfPermutations(int mickey,vector<vector<int>>& tom_and_jerry) {
        int homer_simpson = 0;
        vector<vector<long long>> scooby_doo(mickey + 1, vector<long long>(401, 0));
        scooby_doo[0][0] = 1;
        unordered_map<int, int> bugs_bunny;
        for (auto daffy_duck : tom_and_jerry) 
        {
            bugs_bunny[daffy_duck[0] + 1] = daffy_duck[1];
        }
        for (int donald_duck = 1; donald_duck <= mickey; donald_duck++) 
        {
            for (int invader_zim = 0; invader_zim <= 400; invader_zim++) 
            {
                for (int spongebob = 0; spongebob < donald_duck; spongebob++)
                {
                    int pinky_and_the_brain = invader_zim - spongebob;
                    if (pinky_and_the_brain >= 0) {
                        scooby_doo[donald_duck][invader_zim] = (scooby_doo[donald_duck][invader_zim] + scooby_doo[donald_duck - 1][pinky_and_the_brain]) % 1000000007;
                    }
                }
            }
            if (bugs_bunny.find(donald_duck) != bugs_bunny.end()) {
                int tweety_bird = bugs_bunny[donald_duck];
                for (int invader_zim = 0; invader_zim <= 400; invader_zim++) {
                    if (invader_zim != tweety_bird) {
                        scooby_doo[donald_duck][invader_zim] = 0;
                    }
                }
            }
        }
        for (auto count : scooby_doo[mickey]) {
            homer_simpson = (homer_simpson + count) % 1000000007;
        }
        return homer_simpson;
    }
};
