class Solution(object):
    def numberOfPermutations(self, c, hg):
        q = 1000000007
        t = 400
        e = {}
        for h in hg:
            e[h[0] + 1] = h[1]
        d = [[0] * (t + 1) for _ in range(c + 1)]
        d[0][0] = 1
        for z in range(1, c + 1):
            for b in range(t + 1):
                for k in range(z):
                    u = b - k
                    if u >= 0:
                        d[z][b] = (d[z][b] + d[z - 1][u]) % q
            if z in e:
                y = e[z]
                for b in range(t + 1):
                    if b != y:
                        d[z][b] = 0
        w = 0
        for o in d[c]:
            w = (w + o) % q
        return w
