const int mod= 1000000007;
const int M= 400;

class Solution {
public:
   int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<int> mp(n + 1, -1);

        for (auto req : requirements) {
            mp[req[0]+1]=req[1];
        }

        vector<vector<long long>> dp(n + 1, vector<long long>(M + 1, 0));

           dp[0][0] = 1;

        for (int i = 1; i <= n; i++ ) {
            for (int j = 0; j <= M; j++ ) {
                for (int k = 0; k < i; k++ ) {
                    if ((j-k) >= 0) {
                        dp[i][j] = (dp[i][j] + dp[i - 1][(j-k)]) % mod;
                    }
                }
            }

            if (mp[i] != -1) {
                for (int j = 0; j <= M; j++ ) {
                    if (j != mp[i]) {
                        dp[i][j] = 0;
                    }
                }
            }
        }

        long long ans = 0;
        for (long long x : dp[n]) {
            ans = (ans + x) % mod;
        }

        return ans;
}
};
