/**
 * @param {number} n
 * @param {number[][]} requirements
 * @return {number}
 */

 const MOD = 1e9 + 7;
 const MI = 400;

var numberOfPermutations = function(n, requirements) {
    const reqMap = new Map();
    for (const [req, inv] of requirements) {
        reqMap.set(req + 1, inv);
    }

    const permCounts = new Array(n + 1).fill(0).map(() => new Array(MI + 1).fill(0));
    permCounts[0][0] = 1;

    for (let length = 1; length <= n; length++) {
        for (let inv = 0; inv <= MI; inv++) {
            for (let newPos = 0; newPos < length; newPos++) {
                const prevInv = inv - newPos;
                if (prevInv >= 0) {
                    permCounts[length][inv] = (permCounts[length][inv] + permCounts[length - 1][prevInv]) % MOD;
                }
            }
        }
    if (reqMap.has(length)) {
            const targetInv = reqMap.get(length);
            for (let inv = 0; inv <= MI; inv++) {
                if (inv !== targetInv) {
                    permCounts[length][inv] = 0;
                }
            }
        }
    }

    let result = 0;
    for (const count of permCounts[n]) {
        result = (result + count) % MOD;
    }

    return result;
};
