typedef long long ll;

#define pb push_back
#define sz(x) (int)x.size()
#define all(x) begin(x),end(x)
#define lb(x,y) lower_bound(all(x),y)-begin(x)
class Solution {
public:
    int mod = 1000000007;
    int numberOfPermutations(int n, vector<vector<int>>& req) {
        int mxCnt = 0;
        for (auto &p : req) {
            mxCnt = max(mxCnt, p[1]);
        }
        vector<int> dp(mxCnt + 1, 0), nxt(mxCnt + 1, 0);
        dp[0] = 1;
        vector<int> v(n, -1);
        for (auto &p : req) {
            v[p[0]] = p[1];
        }
        for (int i = 0; i < n; i++) {
            fill(all(nxt), 0);
            fill(all(nxt), 0);
            for (int j = 0; j <= mxCnt; j++) {
                for (int k = 0; k <= i; k++) {
                    if (j + k <= mxCnt) {
                        nxt[j + k] += dp[j];
                        if (nxt[j + k] >= mod) nxt[j + k] -= mod;
                    }
                }
            }
            swap(dp, nxt);
            for (int j = 0; j <= mxCnt; j++) {
                if (v[i] != -1 && v[i] != j) dp[j] = 0;
            }
        }
        int res = 0;
        for (int i : dp) res += i, res %= mod;
        return res;
    }
};
