class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<vector<int>> dp(n + 5, vector<int>(500, 0));
        dp[0][0] = 1;
        vector<int> b(n + 5, -1);
        for(int i = 0; i < requirements.size(); i++) {
            b[requirements[i][0]] = requirements[i][1];
        }

        if(b[0] != -1 && b[0] != 0) return 0;
        for(int i = 1; i < n; i++) {
            for(int j = 0; j <= 400; j++) {
                for(int t = 0; t <= min(i, j); t++) {
                    dp[i][j] += dp[i - 1][j - t];
                    dp[i][j] %= 1000000007;
                }
            }
            if(b[i] != -1) {
                for(int j = 0; j <= 400; j++) {
                    if(b[i] != j) dp[i][j] = 0;
                }
            }
        }
        int ans = 0;
        for(int i = 0; i <= 400; i++) ans = ans + dp[n - 1][i];
        return ans;
    }
};
