class Solution {
public:
    int mod = 1e9 + 7;
    int numberOfPermutations(int n, vector<vector<int>>& req) {
        int ans = 0;
        unordered_map<int, int> mp;
        for(auto i: req){
            mp[i[0]+1] = i[1];
        }

        vector<vector<int>> dp(n+1, vector<int>(401, 0));
        dp[0][0] = 1;

        for(int i=1; i<=n; i++) { // len
            for(int j=0; j<=400; j++) { // no of inv
                for(int k=0; k<i; k++) {
                    int prev = j-k;
                    if(prev>=0) {
                        dp[i][j] = (dp[i][j] + dp[i - 1][prev]) % mod;
                    }
                }
            }
            if(mp.count(i)) {
                for(int j=0; j<=400; j++) {
                    if(j!=mp[i]) dp[i][j] = 0;
                }
            }
        }
        for(auto res:dp[n]) {
            ans = (ans+res)% mod;
        }

        return ans;
    }
};
