#define MOD 1000000007
#define ll long long int
class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& reqs) {
        int maxjCount = 400;
        unordered_map<int, int> r;
        for (auto req : reqs) {
            r[req[0] + 1] = req[1];
        }
        vector<vector<ll>> pc(n + 1, vector<ll>(maxjCount + 1, 0));
        pc[0][0] = 1;
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= maxjCount; j++) {
                for (int pos = 0; pos < i; pos++) {
                    int prevj = j - pos;
                    if (prevj >= 0) {
                        pc[i][j] = (pc[i][j] + pc[i - 1][prevj]) % MOD;
                    }
                }
            }
            if (r.count(i)) {
                int targetj = r[i];
                for (int j = 0; j <= maxjCount; j++) {
                    if (j != targetj) {
                        pc[i][j] = 0;
                    }
                }
            }
        }
        ll res = 0;
        for (ll count : pc[n]) {
            res = (res + count) % MOD;
        }
        return res;
    }
};
