class Solution {
public:
  int numberOfPermutations(int n, vector<vector<int>>& requirements) {
    const int Mod = 1000000007;
    int m = 0;
    for (int i = 0; i < requirements.size(); ++i) {
      m = max(m, requirements[i][1]);
    }
    sort(requirements.begin(), requirements.end());
    vector<vector<int>> a(n + 1, vector<int>(m + 4));
    a[0][0] = 1;
    int p = 0;
    for (int i = 0; i < n; ++i) {
      for (int j = 0; j <= m; ++j) if (a[i][j]) {
        // cout << i << ' ' << j << ' ' << a[i][j] << endl;
        for (int u = 0; u <= i; ++u) {
          if (j + u > m) break;
          (a[i + 1][j + u] += a[i][j]) %= Mod;
        }
      }
      if (i == requirements[p][0]) {
        for (int j = 0; j <= m; ++j) if (j != requirements[p][1]) a[i + 1][j] = 0;
        ++p;
      }
    }
    return a[n][requirements.back()[1]];
  }
};
