class Solution {
public:
    long long mod=1e9+7;
    int numberOfPermutations(int n, vector<vector<int>>& req) {
        unordered_map<long long ,long long> mp;
        long long ans=0;
        vector<vector<long long>> dp(n+5,vector<long long>(410,0));dp[0][0]=1;
        for(auto r:req){
            mp[r[0]]=r[1];
        }
        for(int i=1;i<=n;i++)
        {
            for(int inversion=0;inversion<=401;inversion++)
            {
                for(int j=0;j<min(i,inversion+1);j++)
                {

                        dp[i][inversion]=(dp[i][inversion]%mod+dp[i-1][inversion-j]%mod)%mod;

                }
            }
            if(mp.find(i-1)!=mp.end())
            {
                for(int inversion=0;inversion<=401;inversion++)
                {
                    if(inversion!=mp[i-1])dp[i][inversion]=0;
                }
            }
        }
        ans=dp[n][mp[n-1]];
        return ans;
    }
};
