const int MOD = 1e9 + 7;

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int maxK = 0;
        for (auto it: requirements) {
            maxK = max(maxK,it[1]);
        }
        vector<vector<int>> dp(n + 1, vector<int>(maxK + 1, 0));
        dp[0][0] = 1; 
        for (int i = 1; i <= n; ++i) {
            for (int k = 0; k <= maxK; ++k) {
                for (int p = 0; p <= min(k, i - 1); ++p) {
                    dp[i][k] = (dp[i][k] + dp[i - 1][k - p]) % MOD;
                }
            }
            for(auto it:requirements){
                if(it[0]==i-1){
                    for(int j=0;j<=maxK;j++){
                        if(j!=it[1]){
                            dp[i][j]=0;
                        }
                    }
                }
            }
        }
        int ans=0;
        int i=0;
        for(auto it:dp[n]){
            ans+=dp[n][i];
            i++;
        }


        return ans;
    }
};
