package main

import "fmt"

const (
	MOD            = 1000000007
	MAX_INVERSIONS = 400
)

func numberOfPermutations(n int, req [][]int) int {
	rm := make(map[int]int)
	for _, req := range req {
		rm[req[0]+1] = req[1]
	}

	pc := make([][]int64, n+1)
	for i := range pc {
		pc[i] = make([]int64, MAX_INVERSIONS+1)
	}
	pc[0][0] = 1

	for length := 1; length <= n; length++ {
		for inv := 0; inv <= MAX_INVERSIONS; inv++ {
			for newPos := 0; newPos < length; newPos++ {
				prevInv := inv - newPos
				if prevInv >= 0 {
					pc[length][inv] = (pc[length][inv] + pc[length-1][prevInv]) % MOD
				}
			}
		}

		if targetInv, ok := rm[length]; ok {
			for inv := 0; inv <= MAX_INVERSIONS; inv++ {
				if inv != targetInv {
					pc[length][inv] = 0
				}
			}
		}
	}

	re := int64(0)
	for _, count := range pc[n] {
		re = (re + count) % MOD
	}

	return int(re)
}
