using ll = long long;
class Solution
{
    int mod = 1000000007;
public:
    int numberOfPermutations(int n, vector<vector<int>> &requirements)
    {
        ll ans = 0;
        map<int, int> mp;
        for (auto &i : requirements)  mp[i[0] + 1] = i[1];

        vector<vector<ll>> dp(n + 1, vector<ll>(400 + 5, 0));dp[0][0] = 1;

        for (int i = 1, curIn = 0; i <= n; i++, curIn = 0)
        {
            for (int j = 0; curIn <= 400; j = 0, curIn++)
            {
                while (j < i)
                {
                    int prevIn = curIn - j++;
                    if (prevIn >= 0)    dp[i][curIn] = (dp[i][curIn] + dp[i - 1][prevIn]) % mod;
                }
            }

            if (mp.count(i))
            {
                for(int req = mp[i], j = 0; j <= 400; j++) if (j != req)   dp[i][j] = 0;
            }
        }
        for(int i = 0; i <= 400; i++)    ans = (ans + dp[n][i]) % mod;
        return int(ans);
    }
};
