#define ll long long int
class Solution {
public:  
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        const int MOD = 1e9 + 7;
        unordered_map<int, int> mp;
        for (auto& req : requirements) {
            mp[req[0] + 1] = req[1];
        }

        vector<vector<long long>> cnt(n + 1, vector<ll>(405, 0));
        cnt[0][0] = 1;

        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= 400; j++) {
                for (int k = 0; k < i; k++) {
                    int p = j - k;
                    if (p >= 0) {
                        cnt[i][j] = (cnt[i][j] + cnt[i - 1][p]) % MOD;
                    }
                }
            }

            if (mp.count(i)) {
                int t = mp[i];
                for (int j = 0; j <= 400; j++) {
                    if (j != t) {
                        cnt[i][j] = 0;
                    }
                }
            }
        }

        ll result = 0;
        for (int i = 0; i <= 400; i++) {
            result = (result + cnt[n][i]) % MOD;
        }

        return result;
    }
};
