class Solution {
public:
     const int mod = 1e9 + 7;
      const int maxi = 400;



    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        long long s=0;
          unordered_map<long long, long long>m;
    for ( auto  it : requirements) {
        int z=it[0]+1;
        m[z] = it[1];
    }


    vector<vector<long long>>a(n + 1, vector<long long>(maxi + 1, 0));
    a[0][0] = 1ll;

    for (int i = 1; i <= n; i++) {
        for (int j = 0; j <= maxi; j++) {
            for (int k = 0; k <=i-1; k++) {
                int rec = j-k;
                if (j-k >= 0) {
                    a[i][j] = ( a[i - 1][rec]+a[i][j]) % mod;
                }
            }
        }
        int flag=0;
        int ans=0;
        if (m.find(i) != m.end()) {
            flag=1;
            int z = m[i];
            for (int j = 0; j <= maxi; j++) {
                if (j != z) {
                    a[i][j] = 0;
                }
                else{
                    continue;
                }
            }

            if(flag)ans=1;
        }
    }

        for(int i=0;i<a[n].size();i++){
            s=(s+a[n][i])%mod;
        }


    return s;
    }
};
