from functools import cache
MOD = 10**9+7
class Solution:
    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        req = { k:v for k,v in requirements }

        @cache
        def DP(n, cnt):
            if n == 0 :
                if cnt == 0:
                    return 1
                return 0 
            # 0..n-1
            if req.get(n-1) is not None and req[n-1] != cnt: return 0
            res = 0 
            for i in range(n):
                x = n-1-i
                if x > cnt: continue 
                res += DP(n-1, cnt-x)

            return res % MOD

        return DP(n, req[n-1] )
