class Solution:
    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        MOD = 10**9 + 7

        mp = defaultdict(int)
        for end, cnt in requirements:
            mp[end] = cnt

        dp = [[0] * 401 for _ in range(n+1)]
        dp[0][0] = 1

        for i in range(1, n+1):
            for j in range(401):
                for k in range(i):
                    if j - k >= 0:
                        dp[i][j] += dp[i-1][j-k]
                        dp[i][j] %= MOD
            if (i - 1) in mp:
                cnt = mp[i - 1]
                bak = dp[i][cnt]
                dp[i] = [0] * 401
                dp[i][cnt] = bak

        return sum(dp[n]) % MOD
