/**
 * @param {number} n
 * @param {number[][]} requirements
 * @return {number}
 */
var numberOfPermutations = function(n, requirements) {
    const MOD = 1_000_000_007;
    const MAX_INV = 400;

    const reqMap = new Map();
    for (let [position, inv] of requirements) {
        reqMap.set(position + 1, inv);
    }

    const dp = Array.from({ length: n + 1 }, () => Array(MAX_INV + 1).fill(0));
    dp[0][0] = 1;

    for (let i = 1; i <= n; i++) {
        for (let j = 0; j <= MAX_INV; j++) {
            for (let k = 0; k < i; k++) {
                const prevInv = j - k;
                if (prevInv >= 0) {
                    dp[i][j] = (dp[i][j] + dp[i - 1][prevInv]) % MOD;
                }
            }
        }

        if (reqMap.has(i)) {
            const targetInv = reqMap.get(i);
            for (let j = 0; j <= MAX_INV; j++) {
                if (j !== targetInv) {
                    dp[i][j] = 0;
                }
            }
        }
    }

    let result = 0;
    for (let j = 0; j <= MAX_INV; j++) {
        result = (result + dp[n][j]) % MOD;
    }

    return result;
};
