class Solution {
public:
    lli dp[305][405];
    lli MOD = 1e9 + 7;

    int numberOfPermutations(int n, vector<vector<int>>& req) {
        map<int, int> mp;
        for (auto &i: req) mp[i[0]+1] = i[1];
        memset(dp, 0, sizeof dp);
        dp[0][0] = 1;

        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= 400; j++) {
                for (int k = 1; k <= i; k++) {
                    int len = i-k;
                    int inv = len * (len + 1) / 2;
                    if (j-len >= 0) {
                        if (mp.find(i-1) == mp.end())
                            (dp[i][j] += dp[i-1][j-len]) %= MOD;
                        else {
                            if (j-len == mp[i-1]) (dp[i][j] += dp[i-1][j-len]) %= MOD;
                        }
                    }
                }
            }
        }
        return dp[n][mp[n]];
    }
};
