long long mod = 1e9 + 7;

lli modmul(lli a, lli b)
{
    return (1LL * (a % mod) * (b % mod)) % mod;
}

lli modadd(lli a, lli b)
{
    return ((1LL * a % mod) + (1LL * b % mod)) % mod;
}

class Solution
{
public:
    int numberOfPermutations(int n, vector<vector<int>> &a)
    {
        unordered_map<int, int> mp;
        for (auto x : a)
            mp[x[0] + 1] = x[1];

        vector<vlli> cnt(n + 1, vlli(401, 0));
        cnt[0][0] = 1;

        rep(i, 1, n)
        {
            rep(j, 0, 400) loop(val, i)
            {
                int prev = j - val;
                if (prev >= 0)
                    cnt[i][j] = modadd(cnt[i][j], cnt[i - 1][prev]);
            }

            if (mp.count(i))
            {
                int val = mp[i];
                rep(j, 0, 400) if (j != val)
                    cnt[i][j] = 0;
            }
        }

        lli ans = 0;
        for (auto x : cnt[n])
            ans = modadd(ans, x);

        return ans;
    }
};
