#include<bits/stdc++.h>
class Solution {
public:
    const int mod=1e9+7;
    const int maxi=400;
    int numberOfPermutations(int n, vector<vector<int>>& req) {
        unordered_map<int,int>mp;
        for(auto it:req){
            mp[it[0]+1]=it[1];
        }
        vector<vector<long long int>>dp(n+1,vector<long long int>(maxi+1,0));
        dp[0][0]=1;

        for(int i=1;i<=n;i++){
            for(int j=0;j<=maxi;j++){
                for(int k=0;k<i;k++){
                    int prev=j-k;
                    if(prev<0){
                        continue;
                    }
                    else{
                    dp[i][j]=(dp[i][j]+dp[i-1][prev])%mod;
                    }
                }
            }
            if(mp.count(i)>0){
                int p=mp[i];
                for(int j=0;j<=maxi;j++){
                    if(j!=p){
                        dp[i][j]=0;
                    }
                }
            }
        }
        long long int ans=0;
        for(int j=0;j<=maxi;j++){
            ans=(ans+dp[n][j])%mod;
        }
        return (int)ans;
    }
};
