#include <bits/stdc++.h>
using namespace std;
#define MOD 1000000007


class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& arr) {
        map<int,int> ump;
        for(auto& i:arr) ump[i[0] + 1] = i[1];
        vector<vector<long long> > dp(n+1,vector<long long>(401,0));
        dp[0][0] = 1;
        for(int i=1;i<=n;i++){
            for(int cnt=0;cnt<=400;cnt++){
                for(int j=0;j<i;j++){
                    if(cnt - j >= 0) dp[i][cnt] = (dp[i][cnt] + dp[i - 1][cnt - j])%MOD;
                }
            }
            if(ump.count(i) > 0){
                for(int cnt=0;cnt<=400;cnt++){
                    if(cnt != ump[i]) dp[i][cnt] = 0;
                }
            }
        }
        long long Ans = 0;
        for (auto& i:dp[n]) Ans = (Ans+i)%MOD;
        return Ans;
    }
};
