class Solution {
public:
    const int MOD=1e9+7;
    int numberOfPermutations(int n, vector<vector<int>>& r) {
        unordered_map<int,int> mp;
        for(auto x:r) mp[x[0]+1]=x[1];
        vector<vector<int>> dp(n+1,vector<int>(401,0));
        dp[0][0]=1;
        long long ans=0;
        for(int i=1;i<=n;i++){
            for(int j=0;j<=400;j++){
                for(int k=0;k<i;k++){
                    int x=j-k;
                    if(x>=0){
                        dp[i][j]+=(dp[i-1][x]);
                        dp[i][j]%=MOD;
                    }
                }
            }
            if(mp.find(i)!=mp.end()){
                int t=mp[i];
                for(int j=0;j<=400;j++){
                    if(j!=t) dp[i][j]=0;
                }
            }
        }
        for(auto x:dp[n]) {
            ans+=x;
            ans%=MOD;
        }
        return ans%MOD;
    }
};
