typedef long long ll;
const ll mod=1e9+7;
ll F[401];
void ix()
{
	F[0]=F[1]=1;
	for(ll i=2;i<=400;i++)
	F[i]=(F[i-1]*1LL*i)%mod;
}
ll mpw(ll base, ll exp,ll M)
{ 
    // O(LOGEXP) TIME
    if (exp == 0)
        return 1;
    ll res = mpw(base, exp / 2,M);
    if (exp % 2 == 1)
        return (((res * res) % M) * base) % M;
    else
        return (res * res) % M;
}
ll ncr(ll n, ll r)
{
    ll up = F[n] % mod;
    ll d1 = F[n - r] % mod;
    ll d2 = F[r] % mod;
    d1 = mpw(d1, mod - 2, mod) % mod;
    d2 = mpw(d2, mod - 2, mod) % mod;
    up = (up * d1) % mod;
    up = (up * d2) % mod;
    return up;
}
class Solution {
public:

    ll f(ll n,vector<vector<int>>& req)
    {
        map<ll,ll>mp;
        for(auto& x: req)
        {
            x[0]+=1;
            mp[x[0]]=x[1];
        }
        ll dp[n+1][401];
        for(ll i=0;i<n+1;i++)
        {
            for(ll j=0;j<401;j++)
                dp[i][j]=0;
        }
        dp[0][0]+=1;
        for(ll i=1;i<=n;i++)
        {
            ll curr=0;
            while(curr<401)
            {
                ll np=0;
                while(np<i)
                {
                    ll pre=curr;
                    pre-=np;
                    if(pre>=0)
                    dp[i][curr]+=dp[i-1][pre],dp[i][curr]%=mod,np++;
                    else
                    {
                        np++;
                    }
                }
                curr+=1;
            }
            if(mp.find(i)==mp.end())
                continue;
            else
            {
                ll newr=mp[i];
                ll ps=0;
                while(ps<=400)
                {
                    if(newr==ps)
                        ps++;
                    else
                        dp[i][ps]=0,ps++;
                }
            }
        }
        ll ans=0,res=0;
        while(res<=400)
        {
            ans+=dp[n][res];
            ans%=mod;
            res++;
        }
        return ans;

    }
    int numberOfPermutations(int n, vector<vector<int>>& req) 
    {
       ix(); 
       sort(begin(req),end(req));
       ll ans=f(n*1LL,req);
       return ans;
    }
};
