#define ll long long int
class Solution {
public:
    //400 cnt
    ll M=1e9+7;
    ll last=0;
    map<ll,ll> m;
    ll dp[300][401];
    ll rec(ll ind,ll cst,ll n){
        // if(n==0)return 0;
        // if(k==0)return 1;
        // if(dp[n][k]!=-1)return dp[n][k];
        // ll cnt=0;
        // ll check=
        // for(int i=0; i<=min(k,n-1);i++){
        //     cnt=(cnt+rec(n-1,k-i))%M;
        // }
        // return dp[n][k]=cnt%M;
        if(cst>last){
            return 0;
        }
        if(ind==n-1){
            if(cst==last){
                return 1;
            }
            return 0;
        }
        if(m.find(ind)!=m.end() && cst!=m[ind]){
            return 0;
        }
        if(dp[ind][cst]!=-1){
            return dp[ind][cst];
        }
        ll cnt=0;
        for(int i=0;i<=(ind+1);i++){
            cnt=(cnt+rec(ind+1,cst+i,n))%M;
        }
        // cout<<"HI"<<endl;
        return dp[ind][cst]=cnt;
    }
    int numberOfPermutations(int n, vector<vector<int>>& req) {
        ll nm=req.size();
        for(int i=0;i<nm;i++){
            if(req[i][0]==n-1){
                last=req[i][1];

            }
            m[req[i][0]]=req[i][1];
        }
        // cout<<last<<endl;
        memset(dp,-1,sizeof(dp));
        return rec(0,0,n);
    }
};
