int dp[301][45001];
class Solution {
public:
    int modd=1000000007;
    int solve(unordered_map<int,int>& req,int n,int inv){
        if(inv<0) return 0;
        if(req.find(n-1)!=req.end()){
            if(req[n-1]!=inv) return 0;
        }
        if(n==1) {
            if(inv==0)
            return 1;
        else return 0;}
       if(dp[n][inv]!=-1) return dp[n][inv];
       int ans=0;
       for(int i=0;i<n;i++){
           ans=(ans+solve(req,n-1,inv-(n-i-1)))%modd;
       }
       return dp[n][inv]=ans;
    }
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        unordered_map<int,int> req;
        for(int i=0;i<requirements.size();i++){
            req[requirements[i][0]]=requirements[i][1];
        }
        memset(dp,-1,sizeof(dp));
        return solve(req,n,req[n-1]);
    }
};
