MOD = 10**9 + 7
class Solution(object):
    def numberOfPermutations(self, n, reqs):
        MAX_INV = 400
        r = {req[0] + 1: req[1] for req in reqs}

        pc = [[0] * (MAX_INV + 1) for _ in range(n + 1)]
        pc[0][0] = 1

        for length in range(1, n + 1):
            for inv in range(MAX_INV + 1):
                for pos in range(length):
                    prevInv = inv - pos
                    if prevInv >= 0:
                        pc[length][inv] = (pc[length][inv] + pc[length - 1][prevInv]) % MOD

            if length in r:
                targetInv = r[length]
                for inv in range(MAX_INV + 1):
                    if inv != targetInv:
                        pc[length][inv] = 0

        result = sum(pc[n]) % MOD
        return result
