class Solution {
public:
    int mod = 1000000007;
    int e = 400;    
    int numberOfPermutations(int num, vector<vector<int>>& qtn) {
        int ans = 0;
        long long temp_ans = 0;
        vector<vector<long long>> dp(num + 1, vector<long long>(e + 1, 0)); dp[0][0] = 1;
        unordered_map<int, int> mp;

        for (auto a : qtn) 
        {
            mp[a[0]+1]=a[1];
        }

        for (int g = 1; g < num+1; g++) 
        {
            for (int h = 0; h < e+1; h++) 
            {
                for (int i = 0; i < g; i++) 
                {
                    int j = h - i;
                    if (j >= 0) 
                    {
                        dp[g][h] = (dp[g - 1][j]+dp[g][h]) % mod;
                    }
                }
            }

            if (mp.count(g)>0) 
            {
                int t = mp[g];
                for (int h = 0; h < e+1; h++) 
                {
                    if (h != t) 
                    {
                        dp[g][h] = 0;
                    }
                }
            }
        }

        for (auto a : dp[num]) {
            temp_ans = (temp_ans + a) % mod;
        }
        ans = int(temp_ans);

        return ans;
    }
};
