class Solution {
public:
    int mod = 1e9+7;
    using ll = long long;

    int numberOfPermutations(int n, vector<vector<int>>& a) {

        map<ll,ll>m;
        for(auto x:a){
            m[x[0]+1]=x[1];

        }
        ll ans=0;
        vector<vector<ll>>dp(n+1,vector<ll>(401,0));
        dp[0][0]=1;
        for(int i=1;i<=n;i++){
            for(int j=0;j<401;j++){
                for(int k=0;k<i;k++){
                    if(j-k<0)continue;
                    dp[i][j]=(dp[i][j]+dp[i-1][j-k])%mod;
                }
            }
            if(m.find(i)!=m.end()){
               for(int j=0;j<401;j++)
                   if(j!=m[i])dp[i][j]=0;
            }
        }
        for(auto it:dp[n])ans=(ans+it)%mod;
        return ans;

    }
};
