class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        const int MOD = 1e9 + 7;
        vector<vector<int>> dp(n + 1, vector<int>(400 + 1, 0));

        dp[0][0] = 1;  

        for (int i = 1; i <= n; i++) {
            for (int x = 0; x <= 400; x++) {
                for (int j = 0; j < i; j++) {
                    if (x >= j) {
                        dp[i][x] = (dp[i][x] + dp[i-1][i-j]) % MOD;
                    }
                }
            }
        }


        vector<int> reqinversion(n, -1);
        for (const auto& req : requirements) {
            reqinversion[req[0]] = req[1];
        }


        vector<vector<int>> dp2(n + 1, vector<int>(401, 0));
        dp2[0][0] = 1;

        for (int i = 1; i <= n; i++) {
            for (int x = 0; x <= 400; x++) {
                for (int j = 0; j < i; j++) {
                    if (x >= j) {
                        dp2[i][x] = (dp2[i][x] + dp2[i-1][x-j]) % MOD;
                    }
                }
            }
            if (reqinversion[i-1] != -1) {
                for (int x = 0; x <= 400; x++) {
                    if (x != reqinversion[i-1]) {
                        dp2[i][x] = 0;
                    }
                }
            }
        }

        int result = 0;
        for (int x = 0; x <= 400; ++x) {
            result = (result + dp2[n][x]) % MOD;
        }

        return result;
    }
};
