class Solution {
public:
    const int MOD=1e9+7;
    const int MAX=401;
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        map<int,int> mp;
        for (auto &i:requirements){
            mp[i[0]+1]=i[1];
        }
        vector<vector<long long>> count(n+1,vector<long long>(MAX));
        count[0][0]=1;

        for( int i=1;i<n+1;i++){
            for(int j=0;j<MAX;j++){
                for(int k=0;k<i;k++){
                    if(j-k>-1){
                        count[i][j]+=count[i-1][j-k];
                        count[i][j]%=MOD;
                    }
                }
            }
            if(mp.find(i)!=mp.end()){
                for(int a=0;a<MAX;a++){
                    if(a!=mp[i]){
                        count[i][a]=0;
                    }
                }
            }
        }
        long long ans=0;
        for(auto &i:count[n]){
            ans+=i;
            ans%=MOD;
        }
        return ans;

    }



};
