#define pi 3.1415926535897932384626
#define md 1000000007


/*  Modular Multiplication Function  */
ll mod_mul_fun(ll a, ll b) {
  a = a % md; 
  b = b % md; 
  return (((a * b) % md) + md) % md;
}

class Solution {
public:
    /* Comparator for Sorting */ 
    static bool cmp(pair<string,int>& xx, pair<string,int>& yy)
    {
        return xx.second > yy.second;
    }

    ll calculate(int d,int n,vvi &v1){
        int currVal = 0, res1 = 1;
        int val = n + 1;
        ll finalAns = 0*1LL;
        mul k;
        vvl v2(val, vl(401, currVal));
        v2[0][0] = res1;
        H(i, v1){
            int a = i[0];
            int b = i[1];
            a++;
            k[a]=b;
        }

        G(k1,res1,n){
            G(k2,currVal,400){
                F(k3, k1){
                    if((k2-k3) == currVal || currVal<(k2-k3)){
                        int res2;
                        res2 = (v2[k1-res1][(k2-k3)]+v2[k1][k2])%(md);
                        v2[k1][k2] = res2;
                    }
                }
            }
            if(k.find(k1)!=k.end()){
                G(k2,currVal,400){
                    if(k[k1]!=k2){
                        v2[k1][k2] = currVal;
                    }
                }
            }
        }

        H(i, v2[n]){
            ll curr;
            int val1 = i;
            curr = (val1+finalAns)%md;
            finalAns = curr;
        }

        return finalAns;

    }

    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int d=requirements.size();
        return calculate(d,n,requirements);
    }
};
