const int M = 400;
const int MOD=1e9+7;
// Maths
ll fact(ll n)          { if(n==0) return 1; ll res = 1; for (ll i = 2; i <= n; i++) (res = res%MOD * i%MOD)%MOD; return res; }
ll nPr(ll n, ll r)     { return fact(n) / fact(n - r); }
ll nCr(ll n, ll r)     { return fact(n) / (fact(r) * fact(n - r)); }
ll gcd(ll a, ll b)     { if (b == 0) return a; return gcd(b, a % b); }
ll lcm(ll a, ll b)     { return (a * b) / gcd(a, b);}
ll mypow(ll a, ll b)   { ll ans = 1; while(b){ if (b&1) ans = (ans*a) ; b /= 2; a = (a*a); } return ans; }
bool isPrime(ll n)     { if(n <= 1) return false; for(ll i = 2; i <= sqrt(n); i++) if(n % i == 0) return false; return true; }
bool is_bound(int x,int y, int n, int m) {return x >= 0 && x < n && y >= 0 && y < m;}

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        ll res=0;
        ll x=n+1;
        vector<vector<ll>> ans(x, vector<ll>(M+1, 0));
        ans[0][0] = 1;
        unordered_map<int, int> mp;
        for(const auto& x : requirements){
            int an = x[0]+1;
            mp[an]=x[1];
        } 
        for(int i=1; i<=n; ++i){
            for(int j=0; j<=M; ++j){
                for(int k=0; k<i; ++k){
                    int xx= j-k;
                    if(xx>= 0) ans[i][j]=(ans[i][j] + ans[i-1][xx])%MOD;
                }
            }
            if(mp.find(i) != mp.end()){
                int cnt=mp[i];
                for(int j=0; j<=M; ++j) {if(j!=cnt) ans[i][j] = 0;}
            }
        }
        for(ll a :ans[n]) res=(res + a)%MOD;
        return static_cast<int>(res);
    }
};
